"use strict";
/**
 * Calculates the age of a person.
 *
 * @param name - The name of the person.
 * @param age - The age of the person.
 * @returns A string representing the person's name and age.
 */
const ageCalculator = (name, age) => {
    return `Hi, ${name} is ${age} years old`;
};
ageCalculator('John', 30);
